sys_script_include.script:
var TWTempObjectProperty = Class.create();
TWTempObjectProperty.prototype = {
    initialize: function(twTempObjectPropertyGr) {
		if(twTempObjectPropertyGr){
            if(typeof twTempObjectPropertyGr === "string"){
                twTempObjectPropertyGr = this._getGlideRecordWithId(twTempObjectPropertyGr);
            }
			if(twTempObjectPropertyGr + "" === "[object GlideRecord]"){
				this.setFromGr(twTempObjectPropertyGr);
			}else if(twTempObjectPropertyGr + "" === "[object Object]"){
				this.setFromModel(twTempObjectPropertyGr);
			}
		}
    },

	setFromGr: function(twTempObjectPropertyGr){
		this.sysId = twTempObjectPropertyGr.getUniqueValue();
		this.name = twTempObjectPropertyGr.getValue("u_name");
		this.type = twTempObjectPropertyGr.getValue("u_type");
	},

	setFromModel: function(model){
		for(var key in model){
			this[key] = model[key];
		}
	},

	//Internal Functions

	_getReferenceField: function(columnName, twTempObjectPropertyGr){
		var returnObj = {};
		if(columnName){
			returnObj.id = twTempObjectPropertyGr.getValue(columnName);
			returnObj.displayValue = twTempObjectPropertyGr.getDisplayValue(columnName);
		}
		return returnObj;
	},

    _getGlideRecordWithId: function(id){
        var twTempObjectPropertyGr = new GlideRecord("x_156954_two_way_twtemp_object_property");
        if(twTempObjectPropertyGr.get(id)){
            return twTempObjectPropertyGr;
        }
    },

    type: 'TWTempObjectProperty'
};
