sys_script_include.script:
var TWTempObjectPropertyMap = Class.create();
TWTempObjectPropertyMap.prototype = {
    initialize: function(twTempObjectPropertyMapGr) {
		if(twTempObjectPropertyMapGr){
            if(typeof twTempObjectPropertyMapGr === "string"){
                twTempObjectPropertyMapGr = this._getGlideRecordWithId(twTempObjectPropertyMapGr);
            }
			if(twTempObjectPropertyMapGr + "" === "[object GlideRecord]"){
				this.setFromGr(twTempObjectPropertyMapGr);
			}else if(twTempObjectPropertyMapGr + "" === "[object Object]"){
				this.setFromModel(twTempObjectPropertyMapGr);
			}
		}
    },

	setFromGr: function(twTempObjectPropertyMapGr){
		this.sysId = twTempObjectPropertyMapGr.getUniqueValue();
		this.snTable = twTempObjectPropertyMapGr.getValue("u_sn_table");
		this.snField = twTempObjectPropertyMapGr.getValue("u_sn_field");
		this.twTempProperty = this._getReferenceField("u_twtemp_property", twTempObjectPropertyMapGr);  
	},

	setFromModel: function(model){
		for(var key in model){
			this[key] = model[key];
		}
	},

	//Internal Functions

	_getReferenceField: function(columnName, twTempObjectPropertyMapGr){
		var returnObj = {};
		if(columnName){
			returnObj.id = twTempObjectPropertyMapGr.getValue(columnName);
			returnObj.displayValue = twTempObjectPropertyMapGr.getDisplayValue(columnName);
		}
		return returnObj;
	},

    _getGlideRecordWithId: function(id){
        var twTempObjectPropertyMapGr = new GlideRecord("x_156954_two_way_object_property_map");
        if(twTempObjectPropertyMapGr.get(id)){
            return twTempObjectPropertyMapGr;
        }
    },

    type: 'TWTempObjectPropertyMap'
};
